INSERT INTO `info_informacao` VALUES ('1', 'Sistema de Estágios da Hórus', 'Estagio Horus Marcelo Tabelas', '1', '2012-07-19 04:20:09', '<p>\r\n	Sistema de Est&aacute;gios da H&oacute;rus</p>\r\n<p>\r\n	Tabelas</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	estnc_alunos<br />\r\n	estnc_alunos_avaliacoes<br />\r\n	estnc_alunos_historico<br />\r\n	estnc_areas<br />\r\n	estnc_areas_setores<br />\r\n	estnc_avaliacoes<br />\r\n	estnc_avaliacoes_agendar<br />\r\n	estnc_avaliacoes_respondidas<br />\r\n	estnc_avaliacoes_respostas<br />\r\n	estnc_contratos<br />\r\n	estnc_curriculos_valores<br />\r\n	estnc_curriculos_variaveis<br />\r\n	estnc_cursos<br />\r\n	estnc_cursos_setores<br />\r\n	estnc_departamentos<br />\r\n	estnc_empresas<br />\r\n	estnc_empresas_cadastradas<br />\r\n	estnc_empresas_pessoas<br />\r\n	estnc_estagios<br />\r\n	estnc_grupos<br />\r\n	estnc_grupos_acoes<br />\r\n	estnc_grupos_filhos<br />\r\n	estnc_grupos_perguntas<br />\r\n	estnc_grupos_permissoes<br />\r\n	estnc_grupos_pessoas<br />\r\n	estnc_grupos_regionais<br />\r\n	estnc_grupos_subregionais<br />\r\n	estnc_grup_instituicoes<br />\r\n	estnc_instituicoes_pessoas<br />\r\n	estnc_instituicoes_polos<br />\r\n	estnc_interesse_vaga<br />\r\n	estnc_mensalidades<br />\r\n	estnc_perguntas<br />\r\n	estnc_regionais<br />\r\n	estnc_region_municipios<br />\r\n	estnc_setores<br />\r\n	estnc_setores_departamentos<br />\r\n	estnc_subregionais<br />\r\n	estnc_subregion_bairros<br />\r\n	estnc_supervisores<br />\r\n	estnc_titulos<br />\r\n	estnc_vagas<br />\r\n	estnc_vagas_departamentos<br />\r\n	estnc_visitas</p>\r\n');
INSERT INTO `info_informacao` VALUES ('2', 'Situação de Estágio', 'Situação do estágio - concluído - estnc_estagios - fim do estágio - estagiando - ', '10', '2012-07-19 04:30:34', '<p>\r\n	1 - Todo est&aacute;gio &eacute; registrado no sistema como estagiando pelo processo completo.</p>\r\n<p>\r\n	2 - No cadastro de titulo &eacute; gerado um estagio com a situa&ccedil;&atilde;o selecionada.&nbsp;</p>\r\n<p>\r\n	3 - Se a situa&ccedil;&atilde;o for alterada na tela do gerenciamento de estagio, ela ser&aacute; alterada tamb&eacute;m no titulo. Se for feita na tela de titulo, altera tbm no gerenciamento de est&aacute;gio.</p>\r\n<p>\r\n	4 - Quando o est&aacute;gio termina&nbsp;</p>\r\n<p>\r\n	A situa&ccedil;&atilde;o de est&aacute;gio &eacute; alterada automaticamente ap&oacute;s seu prazo na tela inicial do est&aacute;gio</p>\r\n');
INSERT INTO `info_informacao` VALUES ('3', 'Importação Automática Terminal de Acesso', 'Importação Automática Terminal de Acesso - diario_terminal_acessos - importacao - auto - faltas - terminal de acesso - presença - catracas - automático', '10', '2012-07-19 04:39:18', '<p>\r\n	A importa&ccedil;&atilde;o autom&aacute;tica do terminal de acesso ( Projeto Energia Palho&ccedil;a ), utiliza as seguintes tabelas</p>\r\n<p>\r\n	- diario_terminal_acessos</p>\r\n<p>\r\n	- diario_lista_presenca</p>\r\n<p>\r\n	- diario_aulas_alunos</p>\r\n<p>\r\n	Sempre que o periodo &eacute; finalizado triggers s&atilde;o executadas em base para executar o fechamento do periodo</p>\r\n');
